import Message from "./message";
import {useRef} from "react";


function ChatBox({listMessages}) {
    let window = useRef(null);
    var dataList;
    if (listMessages) {
        dataList = listMessages.map((message, key) => {
                if (key === listMessages.length - 1) {
                    return (
                        <Message
                            sender={message.sent}
                            type={message.type}
                            value={message.content}
                            isTypingAnimation={true}
                            key={key}
                        />
                    );
                } else {
                    return (
                        <Message
                            sender={message.sent}
                            type={message.type}
                            value={message.content}
                            isTypingAnimation={false}
                            key={key}
                        />
                    );
                }
        });

        dataList.reverse();
    }

    return (
        <div className="chat-box scrollbar" ref={window}>
            {dataList}
        </div>
    )
}

export default ChatBox;
